
public class number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int c=1; c<6; c++) {
	         for (int o=4; o>=c; o--) {
	            System.out.print(" ");
	         }
	         for (int k=c; k>=1; k--) {
	            System.out.print(k);
	         }
	         for (int e=2; e<=c; e++) {
	            System.out.print(e);
	         }
	         System.out.println(" ");
	      }
	}

}
